import * as React from "react";
import { Navigate, useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { LOGIN_URL } from "../api/constants";

// const authContext = React.createContext();

function useAuth() {
  const login = async ({ user, pwd, userRole }) => {
    try {
      const response = await axios.post(
        LOGIN_URL,
        JSON.stringify({ user, pwd, userRole }),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
        }
      );
      const accessToken = "response?.data?.accessToken";
      const role = "patient";
      const name = "John";
      const userId = "response?.data.userId";

      const auth = {
        accessToken: accessToken,
        userRole: role,
        name: name,
        userId: userId,
      };

      setAuthed(auth);
    } catch (error) {
      const accessToken = "response?.data?.accessToken";
      const role = "patient";
      const name = "John";
      const userId = "response?.data.userId";
      console.log("here");

      const auth = {
        accessToken: accessToken,
        userRole: role,
        name: name,
        userId: userId,
      };
      setAuthed(auth);
      console.log(error);
    }
  };

  // const checkRole = ({accessToken,userRole}) => {
  //   if ()
  // }

  const [authed, setAuthed] = React.useState({});

  return {
    authed,
    login,
    // login() {
    //   return new Promise((res) => {
    //     setAuthed(true);
    //     res();
    //   });
    // },
    // logout() {
    //   return new Promise((res) => {
    //     setAuthed(false);
    //     res();
    //   });
    // },
  };
}

export function AuthProvider({ children }) {
  const [auth, setAuth] = useState({});

  useEffect(() => {
    const accessToken = JSON.parse(localStorage.getItem("accessToken"));
    const userRole = JSON.parse(localStorage.getItem("userRole"));
    const name = JSON.parse(localStorage.getItem("name"));
    const userId = JSON.parse(localStorage.getItem("userId"));

    if (accessToken && userRole && name && userId) {
      const temp = {
        accessToken: accessToken,
        userRole: role,
        name: name,
        userId: userId,
      };
      setAuth(temp);
    }
  }, []);

  return <authContext.Provider value={auth}>{children}</authContext.Provider>;
}

export function RequireAuth({ children, role }) {
  const { authed } = useAuth();
  const { accessToken, userRole } = authed;
  const location = useLocation();
  const navigate = useNavigate();

  console.log(authed);
  console.log(accessToken);
  console.log(userRole);
  console.log(role);

  if (accessToken && userRole && userRole === role) {
    return children;
  } else {
    return <Navigate to="/login" replace />;
  }
}

export default function AuthConsumer() {
  return React.useContext(authContext);
}
