import { useAuth } from "../context/AuthProvider";
import { useNavigate } from "react-router-dom";
import styles from "./Patient_Dashboard.module.css";
import axios from "axios";
import { VIEW_RECORDS_URL } from "../api/constants";

const View_Records = () => {
  const navigate = useNavigate();
  const name = localStorage.getItem("name");
  const userId = localStorage.getItem("userId");
  const accessToken = localStorage.getItem("accessToken");

  const response = {
    healthRecords: {
      name: "Patient Kelly",
      dateofbirth: "1999-01-08",
      height: 155,
      weight: 53,
      bloodtype: "B+",
      allergies: "None",
    },
    examRecords: [
      {
        session_id: "123",
        doctor: "Dr Jim",
        diagnosis: "High Fever",
        prescription: "150mg panadol",
        sessiontime: "2022-09-22T16:56:36.636524+08:00",
      },
      {
        session_id: "321",
        doctor: "Dr Jim",
        diagnosis: "Stomachache",
        prescription: "150mg paracetamol",
        sessiontime: "2022-09-22T16:57:08.848481+08:00",
      },
    ],
  };

  function renderUsers() {
    const userList = [];
    for (let i = 0; i < this.state.users.length; i++) {
      let name = `${this.state.users[i].name.first} ${this.state.users[i].name.last}`;
      let avatar = this.state.users[i].picture.thumbnail;
      let email = this.state.users[i].email;
      let key = this.state.users[i].id.value;
      userList.push(
        <User name={name} avatar={avatar} email={email} key={key} />
      );
    }

    return userList;
  }

  console.log(response.healthRecords);
  console.log(response.examRecords.length);

  // axios
  //   .post(
  //     VIEW_RECORDS_URL,
  //     {
  //       token: accessToken,
  //       userId: userId,
  //     },
  //     {
  //       headers: { "Content-Type": "application/json" },
  //       withCredentials: true,
  //     }
  //   )
  //   .then(function (response) {
  //     console.log(response);
  //   })
  //   .catch(function (error) {
  //     console.log(error);
  //   });

  const Back = async () => {
    navigate("/patient");
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.header}>Health Records for {name}</h2>
      <br></br>
      <table>
        <tr>
          <th>Name</th>
          <th>{response.healthRecords.name}</th>
        </tr>
        <tr>
          <th>Date of Birth</th>
          <th>{response.healthRecords.dateofbirth}</th>
        </tr>
        <tr>
          <th>Height</th>
          <th>{response.healthRecords.height}cm</th>
        </tr>
        <tr>
          <th>Weight</th>
          <th>{response.healthRecords.weight}.kg</th>
        </tr>
        <tr>
          <th>Blood Type</th>
          <th>{response.healthRecords.bloodtype}</th>
        </tr>
        <tr>
          <th>Allergies</th>
          <th>{response.healthRecords.allergies}</th>
        </tr>
      </table>
      <br></br>
      <h2 className={styles.header}>Session Records for {name}</h2>
      <table>
        {response.examRecords.map((data, index) => (
          <p>{data.name}</p>
        ))}
      </table>
      <div class={styles.buttons_container}>
        <button class={styles.button} onClick={Back}>
          Back
        </button>
      </div>
    </div>
  );
};
export default View_Records;
